    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        text = jTextArea1.getText();
        obj = new backend(text);
        jTextField1.setText(obj.getTextLength() + "");
        jTextField2.setText(obj.getWordCount() + "");
        jTextField3.setText(obj.getTabCount() + "");
        jTextField5.setText(obj.getSpaceCount() + "");
        jTextField6.setText(obj.getLineCount() + "");
        
        String Text = jTextArea1.getText();
        String Special_char = "";
        for(int i = 0 ; i < Text.length(); i++)
        {
           if(Special_char.contains(Text.charAt(i) + "")){
               Special_char += "";
           }else{
               Special_char += Text.charAt(i);
           }
        }
        for(int i = 0; i < Special_char.length(); i++){
            jComboBox1.addItem(Special_char.charAt(i) + "");
        }

        
    }//GEN-LAST:event_jButton1ActionPerformed

