    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        String selectedItem = jComboBox1.getSelectedItem() + "";
        int count = 0;
        for(int i = 0; i < obj.getTextLength(); i++){
            if(selectedItem.equals(text.charAt(i) + ""))count++;
        }
        jTextField4.setText(count  + "");
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        String word = jTextField7.getText().toLowerCase();
        String[] words = text.split("[\s\t\n]");
        int count = 0;
        for(int i = 0; i < words.length; i++){
            if(word.equals(words[i].toLowerCase()))count++;
        }
        jTextField8.setText(count + "");
    }//GEN-LAST:event_jButton3ActionPerformed

